import { reservationApi, equipmentApi } from '@/api';
import { isReservationExpired } from '@/utils/date';
export default {
  name: 'ReservationQuery',
  data() {
    return {
      activeTab: 'public',
      // 默认显示公开查询选项卡

      // 公开查询相关
      publicLoading: false,
      publicQuerySubmitted: false,
      publicReservations: [],
      categories: [],
      publicQueryForm: {
        category: '',
        dateRange: null
      },
      // 个人预约管理相关
      personalLoading: false,
      showInstructions: true,
      notFound: false,
      personalQueryForm: {
        reservationCode: '',
        userContact: ''
      },
      personalQueryRules: {
        reservationCode: [{
          required: false,
          message: this.$t('reservation.requiredField'),
          trigger: 'blur'
        }, {
          min: 6,
          max: 20,
          message: this.$t('common.lengthLimit', {
            min: 6,
            max: 20
          }),
          trigger: 'blur'
        }],
        userContact: [{
          required: false,
          message: this.$t('reservation.requiredField'),
          trigger: 'blur'
        }]
      }
    };
  },
  created() {
    // 如果URL中有预定码参数，自动填充并切换到个人预约管理选项卡
    const code = this.$route.query.code;
    if (code) {
      this.personalQueryForm.reservationCode = code;
      this.activeTab = 'personal';
    }

    // 获取设备类别
    this.fetchCategories();

    // 默认加载公开查询数据
    this.handlePublicQuery();
  },
  methods: {
    // 获取设备类别
    async fetchCategories() {
      try {
        const response = await equipmentApi.getCategories();
        this.categories = response.data.categories.map(item => item.category);
      } catch (error) {
        console.error('Failed to fetch equipment categories:', error);
      }
    },
    // 处理选项卡切换
    handleTabClick(tab) {
      if (tab.name === 'public') {
        this.handlePublicQuery();
      } else {
        this.showInstructions = true;
        this.notFound = false;
      }
    },
    // 处理公开查询
    async handlePublicQuery() {
      this.publicLoading = true;
      this.publicQuerySubmitted = true;
      try {
        // 准备查询参数
        const params = {
          limit: 100 // 设置较大的限制以获取更多结果
        };

        // 添加类别过滤
        if (this.publicQueryForm.category) {
          // 需要先获取该类别的所有设备ID
          const equipmentResponse = await equipmentApi.getEquipments({
            category: this.publicQueryForm.category,
            limit: 100
          });
          if (equipmentResponse.data.items.length > 0) {
            // 如果有设备，使用第一个设备的ID进行查询
            // 注意：这里可以改进，应该查询该类别的所有设备的预约
            params.equipment_id = equipmentResponse.data.items[0].id;
          }
        }

        // 添加日期范围过滤
        if (this.publicQueryForm.dateRange && this.publicQueryForm.dateRange.length === 2) {
          const [startDate, endDate] = this.publicQueryForm.dateRange;
          params.from_date = startDate.toISOString();

          // 将结束日期调整为当天的最后一秒
          const endDateObj = new Date(endDate);
          endDateObj.setHours(23, 59, 59, 999);
          params.to_date = endDateObj.toISOString();
        }

        // 发送请求
        const response = await reservationApi.getReservations(params);
        this.publicReservations = response.data.items;
      } catch (error) {
        console.error('Failed to query public reservations:', error);
        this.$message.error(this.$t('error.queryFailed'));
        this.publicReservations = [];
      } finally {
        this.publicLoading = false;
      }
    },
    // 处理个人预约查询
    handlePersonalQuery() {
      // 自定义验证：预定码和联系方式至少填写一项
      if (!this.personalQueryForm.reservationCode && !this.personalQueryForm.userContact) {
        this.$message.error(this.$t('reservation.atLeastOneField'));
        return false;
      }
      this.$refs.personalQueryForm.validate(async valid => {
        if (!valid) {
          return false;
        }
        this.personalLoading = true;
        this.showInstructions = false;
        this.notFound = false;
        try {
          // 如果有预定码，优先使用预定码查询
          if (this.personalQueryForm.reservationCode) {
            try {
              const response = await reservationApi.getReservationByCode(this.personalQueryForm.reservationCode);
              if (response.data.success) {
                // 导航到预定详情页
                this.$router.push(`/reservation/${this.personalQueryForm.reservationCode}`);
                return;
              }
            } catch (error) {
              console.error('Failed to query by reservation code:', error);
            }
          }

          // 如果没有预定码或预定码查询失败，尝试使用联系方式查询
          if (this.personalQueryForm.userContact) {
            try {
              // 这里需要调用后端接口根据联系方式查询预定
              // 假设有一个根据联系方式查询的API
              const response = await reservationApi.getReservations({
                user_contact: this.personalQueryForm.userContact,
                limit: 10
              });
              if (response.data.items && response.data.items.length > 0) {
                // 如果找到多个预定，可以显示列表让用户选择
                // 这里简化处理，直接使用第一个预定
                const firstReservation = response.data.items[0];
                this.$router.push(`/reservation/${firstReservation.reservation_code}`);
                return;
              }
            } catch (contactError) {
              console.error('Failed to query by contact:', contactError);
            }
          }

          // 如果两种方式都查询失败，显示未找到
          this.notFound = true;
        } catch (error) {
          console.error('Failed to query personal reservation:', error);
          this.notFound = true;
        } finally {
          this.personalLoading = false;
        }
      });
    },
    // 格式化日期时间
    formatDateTime(row, column, cellValue) {
      if (!cellValue) return '';
      const date = new Date(cellValue);
      return `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')} ${String(date.getHours()).padStart(2, '0')}:${String(date.getMinutes()).padStart(2, '0')}`;
    },
    // 获取状态类型
    getStatusType(reservation) {
      // 如果预约已取消，返回灰色
      if (reservation.status === 'cancelled') {
        return 'info';
      }

      // 如果预约已过期，返回橙色
      if (isReservationExpired(reservation.end_datetime)) {
        return 'warning';
      }

      // 如果预约已确认且未过期，返回绿色
      return 'success';
    },
    // 获取状态文本
    getStatusText(reservation) {
      // 如果预约已取消，显示"已取消"
      if (reservation.status === 'cancelled') {
        return this.$t('reservation.cancelled');
      }

      // 如果预约已过期，显示"已过期"
      if (isReservationExpired(reservation.end_datetime)) {
        return this.$t('reservation.expired');
      }

      // 如果预约已确认且未过期，显示"已确认"
      return this.$t('reservation.confirmed');
    },
    // 重置表单
    resetForm() {
      if (this.activeTab === 'public') {
        this.publicQueryForm.category = '';
        this.publicQueryForm.dateRange = null;
        this.handlePublicQuery();
      } else {
        this.$refs.personalQueryForm.resetFields();
        this.notFound = false;
        this.showInstructions = true;
      }
    }
  }
};
