import { mapGetters } from 'vuex';
import { equipmentApi, categoryApi } from '@/api';
import RichTextEditor from '@/components/common/RichTextEditor.vue';
import axios from 'axios';
export default {
  name: 'AdminEquipment',
  components: {
    RichTextEditor
  },
  data() {
    return {
      loading: false,
      submitting: false,
      equipments: [],
      total: 0,
      currentPage: 1,
      pageSize: 10,
      categories: [],
      filter: {
        category: '',
        status: '',
        search: ''
      },
      // 响应式布局相关
      isMobile: window.innerWidth <= 768,
      dialogVisible: false,
      dialogType: 'add',
      // 'add' or 'edit'
      form: {
        id: null,
        name: '',
        category: '',
        model: '',
        location: '',
        status: 'available',
        description: '',
        user_guide: '',
        video_tutorial: '',
        image_path: '',
        allow_simultaneous: false,
        max_simultaneous: 1
      },
      rules: {
        name: [{
          required: true,
          message: this.$t('reservation.requiredField'),
          trigger: 'blur'
        }],
        category: [{
          required: true,
          message: this.$t('reservation.requiredField'),
          trigger: 'change'
        }],
        status: [{
          required: true,
          message: this.$t('reservation.requiredField'),
          trigger: 'change'
        }]
      },
      uploadDialogVisible: false,
      currentEquipment: {},
      imageUrl: '',
      // 上传相关
      uploadUrl: axios.defaults.baseURL + '/api/equipment/upload-image',
      // 视频相关
      videoType: 'youtube',
      // 富文本编辑器选项
      editorOptions: {
        modules: {
          toolbar: [['bold', 'italic', 'underline', 'strike'], ['blockquote', 'code-block'], [{
            'header': 1
          }, {
            'header': 2
          }], [{
            'list': 'ordered'
          }, {
            'list': 'bullet'
          }], [{
            'script': 'sub'
          }, {
            'script': 'super'
          }], [{
            'indent': '-1'
          }, {
            'indent': '+1'
          }], [{
            'direction': 'rtl'
          }], [{
            'size': ['small', false, 'large', 'huge']
          }], [{
            'header': [1, 2, 3, 4, 5, 6, false]
          }], [{
            'color': []
          }, {
            'background': []
          }], [{
            'font': []
          }], [{
            'align': []
          }], ['clean'], ['link', 'image', 'video']]
        },
        placeholder: '',
        theme: 'snow'
      }
    };
  },
  computed: {
    // 获取token
    getToken() {
      return localStorage.getItem('token') || '';
    },
    uploadHeaders() {
      return {
        Authorization: `Bearer ${localStorage.getItem('token') || ''}`
      };
    },
    // 获取完整的图片URL
    baseUrl() {
      return axios.defaults.baseURL || 'http://localhost:8000';
    },
    // 根据屏幕宽度动态调整分页组件布局
    paginationLayout() {
      return this.isMobile ? 'prev, next' : 'total, prev, pager, next';
    }
  },
  created() {
    this.fetchData();
    this.fetchCategories();
    // 添加窗口大小变化的监听器
    window.addEventListener('resize', this.handleResize);
  },
  beforeDestroy() {
    // 移除窗口大小变化的监听器
    window.removeEventListener('resize', this.handleResize);
  },
  methods: {
    // 获取设备列表
    async fetchData() {
      try {
        this.loading = true;
        const params = {
          page: this.currentPage,
          limit: this.pageSize,
          category: this.filter.category || undefined,
          status: this.filter.status || undefined,
          search: this.filter.search || undefined
        };
        const response = await equipmentApi.getEquipments(params);
        this.equipments = response.data.items;
        this.total = response.data.total;
      } catch (error) {
        console.error('获取设备列表失败:', error);
        this.$message.error(this.$t('error.serverError'));
      } finally {
        this.loading = false;
      }
    },
    // 获取设备类别
    async fetchCategories() {
      try {
        const response = await categoryApi.getAllCategories();
        this.categories = response.data.map(item => item.name);
      } catch (error) {
        console.error('获取设备类别失败:', error);
      }
    },
    // 处理筛选条件变化
    handleFilterChange() {
      this.currentPage = 1;
      this.fetchData();
    },
    // 处理页码变化
    handlePageChange(page) {
      this.currentPage = page;
      this.fetchData();
    },
    // 添加设备
    handleAdd() {
      this.dialogType = 'add';
      this.form = {
        id: null,
        name: '',
        category: '',
        model: '',
        location: '',
        status: 'available',
        description: '',
        user_guide: '',
        video_tutorial: '',
        image_path: '',
        allow_simultaneous: false,
        max_simultaneous: 1
      };
      this.dialogVisible = true;
    },
    // 编辑设备
    handleEdit(row) {
      this.dialogType = 'edit';
      this.form = {
        ...row
      };
      this.dialogVisible = true;
    },
    // 删除设备
    handleDelete(row) {
      this.$confirm(this.$t('admin.confirmDeleteEquipment'), this.$t('common.warning'), {
        confirmButtonText: this.$t('common.confirm'),
        cancelButtonText: this.$t('common.cancel'),
        type: 'warning'
      }).then(async () => {
        try {
          this.loading = true;
          await equipmentApi.deleteEquipment(row.id);
          this.$message.success(this.$t('admin.equipmentDeleted'));
          this.fetchData();
        } catch (error) {
          console.error('删除设备失败:', error);
          this.$message.error(this.$t('error.serverError'));
        } finally {
          this.loading = false;
        }
      }).catch(() => {
        // 取消删除，不做任何处理
      });
    },
    // 上传设备图片
    handleUploadImage(row) {
      this.currentEquipment = row;
      this.imageUrl = row.image_path || '';
      this.uploadDialogVisible = true;
    },
    // 提交表单
    submitForm() {
      this.$refs.form.validate(async valid => {
        if (!valid) return;
        try {
          this.submitting = true;
          if (this.dialogType === 'add') {
            // 创建设备
            const response = await equipmentApi.createEquipment(this.form);
            this.$message.success(this.$t('admin.equipmentAdded'));
          } else {
            // 更新设备
            const response = await equipmentApi.updateEquipment(this.form.id, this.form);
            this.$message.success(this.$t('admin.equipmentUpdated'));
          }
          this.dialogVisible = false;
          this.fetchData();
        } catch (error) {
          console.error('保存设备失败:', error);
          this.$message.error(this.$t('error.serverError'));
        } finally {
          this.submitting = false;
        }
      });
    },
    // 重置表单
    resetForm() {
      if (this.$refs.form) {
        this.$refs.form.resetFields();
      }
    },
    // 上传前验证
    beforeUpload(file) {
      const isImage = file.type.startsWith('image/');
      const isLt8M = file.size / 1024 / 1024 < 8;
      if (!isImage) {
        this.$message.error(this.$t('admin.imageTypeError'));
        return false;
      }
      if (!isLt8M) {
        this.$message.error(this.$t('admin.imageSizeError').replace('2MB', '8MB'));
        return false;
      }
      return true;
    },
    // 上传成功（添加/编辑表单中）
    handleUploadSuccess(response) {
      this.form.image_path = response.data.image_url;
      this.$message.success(this.$t('admin.imageUploadSuccess'));
    },
    // 上传成功（单独上传图片对话框）
    handleImageUploadSuccess(response) {
      this.imageUrl = response.data.image_url;
      this.currentEquipment.image_path = response.data.image_url;

      // 更新设备列表中的图片URL
      const index = this.equipments.findIndex(item => item.id === this.currentEquipment.id);
      if (index !== -1) {
        this.$set(this.equipments, index, {
          ...this.currentEquipment
        });
      }
      this.$message.success(this.$t('admin.imageUploadSuccess'));

      // 关闭对话框
      setTimeout(() => {
        this.uploadDialogVisible = false;
      }, 1500);
    },
    // 上传失败
    handleUploadError(error, file) {
      console.error('上传图片失败:', error);
      console.log('文件信息:', file ? {
        name: file.name,
        size: file.size,
        type: file.type,
        lastModified: file.lastModified
      } : 'No file info');

      // 尝试获取更详细的错误信息
      let errorMessage = this.$t('admin.imageUploadError');
      if (error.response && error.response.data) {
        console.error('错误响应数据:', error.response.data);
        if (error.response.data.detail) {
          errorMessage += ': ' + error.response.data.detail;
        }
      }
      this.$message.error(errorMessage);
    },
    // 处理视频类型变化
    handleVideoTypeChange() {
      // 如果已经有视频URL，则根据新的视频类型进行转换
      if (this.form.video_tutorial) {
        // 提取视频ID
        let videoId = '';

        // 尝试从当前的URL中提取视频ID
        if (this.form.video_tutorial.includes('youtube.com') || this.form.video_tutorial.includes('youtu.be')) {
          // 从 YouTube URL 提取视频ID
          const match = this.form.video_tutorial.match(/(?:youtube\.com\/(?:[^\/]+\/.+\/|(?:v|e(?:mbed)?)\/|.*[?&]v=)|youtu\.be\/)([^"&?\/ ]{11})/);
          if (match && match[1]) {
            videoId = match[1];
          }
        } else if (this.form.video_tutorial.includes('bilibili.com')) {
          // 从 Bilibili URL 提取视频ID
          const match = this.form.video_tutorial.match(/bilibili\.com\/video\/([^\/?]+)/);
          if (match && match[1]) {
            videoId = match[1].replace('BV', '');
          }
        }

        // 如果成功提取到视频ID，则根据新的视频类型生成URL
        if (videoId) {
          switch (this.videoType) {
            case 'youtube':
              this.form.video_tutorial = `https://www.youtube.com/embed/${videoId}`;
              break;
            case 'bilibili':
              this.form.video_tutorial = `https://player.bilibili.com/player.html?bvid=BV${videoId}`;
              break;
            default:
              // 其他类型不做处理
              break;
          }
        }
      }
    },
    // 获取完整的图片URL
    getFullImageUrl(url) {
      if (!url) return '';

      // 如果已经是完整URL，直接返回
      if (url.startsWith('http://') || url.startsWith('https://')) {
        return url;
      }

      // 如果是相对路径，添加基础URL
      if (url.startsWith('/')) {
        return this.baseUrl + url;
      }

      // 其他情况，添加基础URL和斜杠
      return this.baseUrl + '/' + url;
    },
    // 触发手动上传
    triggerManualUpload() {
      this.$refs.manualFileInput.click();
    },
    // 处理手动文件选择
    async handleManualFileChange(event) {
      const file = event.target.files[0];
      if (!file) return;

      // 验证文件
      const isImage = file.type.startsWith('image/');
      const isLt8M = file.size / 1024 / 1024 < 8;
      if (!isImage) {
        this.$message.error(this.$t('admin.imageTypeError'));
        return;
      }
      if (!isLt8M) {
        this.$message.error(this.$t('admin.imageSizeError').replace('2MB', '8MB'));
        return;
      }

      // 创建 FormData
      const formData = new FormData();
      formData.append('file', file);
      try {
        this.loading = true;

        // 直接使用 axios 发送请求
        const response = await axios.post(this.uploadUrl, formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${localStorage.getItem('token') || ''}`
          }
        });

        // 处理成功响应
        this.form.image_path = response.data.data.image_url;
        this.$message.success(this.$t('admin.imageUploadSuccess'));
      } catch (error) {
        console.error('手动上传图片失败:', error);
        console.log('文件信息:', {
          name: file.name,
          size: file.size,
          type: file.type,
          lastModified: file.lastModified
        });

        // 尝试获取更详细的错误信息
        let errorMessage = this.$t('admin.imageUploadError');
        if (error.response && error.response.data) {
          console.error('错误响应数据:', error.response.data);
          if (error.response.data.detail) {
            errorMessage += ': ' + error.response.data.detail;
          }
        }
        this.$message.error(errorMessage);
      } finally {
        this.loading = false;
        // 清空文件输入框，允许再次选择同一文件
        this.$refs.manualFileInput.value = '';
      }
    },
    // 触发对话框手动上传
    triggerDialogManualUpload() {
      this.$refs.dialogManualFileInput.click();
    },
    // 处理图片加载失败
    handleImageLoadError(row) {
      console.log('图片加载失败，使用默认图片', row);
      // 如果图片加载失败，将image_path设置为空，这样会显示默认图片
      if (row && row.image_path) {
        // 在Vue中安全地更新对象属性
        this.$set(row, 'image_path', '');
      }
    },
    // 处理对话框手动文件选择
    async handleDialogManualFileChange(event) {
      const file = event.target.files[0];
      if (!file) return;

      // 验证文件
      const isImage = file.type.startsWith('image/');
      const isLt8M = file.size / 1024 / 1024 < 8;
      if (!isImage) {
        this.$message.error(this.$t('admin.imageTypeError'));
        return;
      }
      if (!isLt8M) {
        this.$message.error(this.$t('admin.imageSizeError').replace('2MB', '8MB'));
        return;
      }

      // 创建 FormData
      const formData = new FormData();
      formData.append('file', file);
      if (this.currentEquipment.id) {
        formData.append('equipment_id', this.currentEquipment.id);
      }
      try {
        this.loading = true;

        // 直接使用 axios 发送请求
        const response = await axios.post(this.uploadUrl, formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${localStorage.getItem('token') || ''}`
          }
        });

        // 处理成功响应
        this.imageUrl = response.data.data.image_url;
        this.currentEquipment.image_path = response.data.data.image_url;

        // 更新设备列表中的图片URL
        const index = this.equipments.findIndex(item => item.id === this.currentEquipment.id);
        if (index !== -1) {
          this.$set(this.equipments, index, {
            ...this.currentEquipment
          });
        }
        this.$message.success(this.$t('admin.imageUploadSuccess'));

        // 关闭对话框
        setTimeout(() => {
          this.uploadDialogVisible = false;
        }, 1500);
      } catch (error) {
        console.error('手动上传图片失败:', error);
        console.log('文件信息:', {
          name: file.name,
          size: file.size,
          type: file.type,
          lastModified: file.lastModified
        });

        // 尝试获取更详细的错误信息
        let errorMessage = this.$t('admin.imageUploadError');
        if (error.response && error.response.data) {
          console.error('错误响应数据:', error.response.data);
          if (error.response.data.detail) {
            errorMessage += ': ' + error.response.data.detail;
          }
        }
        this.$message.error(errorMessage);
      } finally {
        this.loading = false;
        // 清空文件输入框，允许再次选择同一文件
        this.$refs.dialogManualFileInput.value = '';
      }
    }
  }
};
