// 数据库表实时查看相关API
import request from './index'

// 获取所有表名
export function getDbTables() {
  return request({
    url: '/api/db/tables',
    method: 'get',
  })
}

// 获取表字段结构
export function getDbTableColumns(tableName) {
  return request({
    url: `/api/db/table/${tableName}/columns`,
    method: 'get',
  })
}

// 分页获取表数据
export function getDbTableRows(tableName, params) {
  return request({
    url: `/api/db/table/${tableName}/rows`,
    method: 'get',
    params,
  })
}
