import { mapGetters, mapActions } from 'vuex';
export default {
  name: 'LanguageSwitcher',
  computed: {
    ...mapGetters(['getLanguage']),
    currentLanguage() {
      return this.getLanguage;
    }
  },
  methods: {
    ...mapActions(['setLanguage']),
    handleLanguageChange(lang) {
      this.setLanguage(lang);
      this.$i18n.locale = lang;
    }
  }
};
