import LanguageSwitcher from '@/components/common/LanguageSwitcher.vue';
export default {
  name: 'MobileNav',
  components: {
    LanguageSwitcher
  },
  data() {
    return {
      isOpen: false
    };
  },
  computed: {
    activeRoute() {
      return this.$route.path;
    }
  },
  methods: {
    toggleMenu() {
      this.isOpen = !this.isOpen;

      // 禁用/启用body滚动
      if (this.isOpen) {
        document.body.style.overflow = 'hidden';
      } else {
        document.body.style.overflow = '';
      }
    },
    closeMenu() {
      this.isOpen = false;
      document.body.style.overflow = '';
    },
    handleSelect(key) {
      this.$router.push(key);
      this.closeMenu();
    }
  },
  // 路由变化时关闭菜单
  watch: {
    '$route'() {
      this.closeMenu();
    }
  },
  // 组件销毁时恢复body滚动
  beforeDestroy() {
    document.body.style.overflow = '';
  }
};
