// import { Calendar } from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import FullCalendar from '@fullcalendar/vue';
import { formatDate } from '@/utils/date';
import reservationApi from '@/api/reservation';
export default {
  name: 'CalendarView',
  components: {
    FullCalendar
  },
  data() {
    return {
      calendarOptions: {
        plugins: [dayGridPlugin, timeGridPlugin, interactionPlugin],
        initialView: 'dayGridMonth',
        headerToolbar: false,
        // 我们使用自定义的头部控件
        events: [],
        // 将通过API加载
        eventClick: this.handleEventClick,
        eventDidMount: this.handleEventDidMount,
        datesSet: this.handleDatesSet,
        locale: this.$i18n.locale === 'zh-CN' ? 'zh-cn' : 'en',
        firstDay: 1,
        // 周一作为一周的第一天
        allDaySlot: false,
        // 不显示"全天"选项
        slotMinTime: '00:00:00',
        // 从0点开始
        slotMaxTime: '24:00:00',
        // 到24点结束
        height: 'auto',
        nowIndicator: true,
        // 显示当前时间指示器
        slotLabelFormat: {
          hour: '2-digit',
          minute: '2-digit',
          hour12: false // 使用24小时制
        },
        slotEventOverlap: false,
        // 禁止事件重叠
        eventTimeFormat: {
          // 统一时间格式
          hour: '2-digit',
          minute: '2-digit',
          hour12: false
        },
        titleFormat: {
          year: 'numeric',
          month: 'long'
        },
        // 标题格式
        views: {
          dayGridMonth: {
            dayHeaderFormat: {
              weekday: 'long'
            },
            // 月视图只显示星期几
            fixedWeekCount: false,
            // 根据当月天数动态调整行数
            showNonCurrentDates: false // 隐藏不属于当前月份的日期
          },
          timeGridWeek: {
            dayHeaderFormat: {
              weekday: 'long',
              month: 'numeric',
              day: 'numeric',
              omitCommas: true
            } // 周视图显示完整日期
          },
          timeGridDay: {
            dayHeaderFormat: {
              weekday: 'long',
              month: 'numeric',
              day: 'numeric',
              omitCommas: true
            },
            // 日视图显示完整日期
            slotEventOverlap: false,
            // 日视图特别禁止事件重叠
            eventMaxStack: 4,
            // 最多显示4个事件，超过则显示"+更多"
            moreLinkClick: 'popover',
            // 点击"+更多"时显示弹窗
            displayEventTime: true,
            // 显示事件时间
            eventDisplay: 'block',
            // 块状显示事件
            dayMaxEventRows: true,
            // 允许日视图中多个事件行
            dayMaxEvents: false,
            // 不限制单日事件数量 
            eventTimeFormat: {
              // 日视图中的时间格式
              hour: '2-digit',
              minute: '2-digit',
              hour12: false
            },
            // 添加日视图的特殊配置，解决事件重叠问题
            resourceAreaWidth: '0',
            // 不显示资源区域
            slotDuration: '00:30:00',
            // 时间槽为30分钟
            snapDuration: '00:15:00',
            // 拖动时的最小间隔为15分钟
            slotLabelFormat: {
              // 时间槽标签格式
              hour: '2-digit',
              minute: '2-digit',
              hour12: false
            }
          }
        },
        locales: {
          'zh-cn': {
            week: {
              dow: 1,
              // 周一作为一周的第一天
              doy: 4 // 一年中第一周必须包含1月4日
            },
            buttonText: {
              today: '今天',
              month: '月',
              week: '周',
              day: '日'
            },
            weekText: '周',
            allDayText: '全天',
            moreLinkText: '更多',
            noEventsText: '没有事件'
          },
          'en': {
            week: {
              dow: 1,
              // 周一作为一周的第一天
              doy: 4 // 一年中第一周必须包含1月4日
            },
            buttonText: {
              today: 'Today',
              month: 'Month',
              week: 'Week',
              day: 'Day'
            },
            weekText: 'W',
            allDayText: 'All day',
            moreLinkText: 'more',
            noEventsText: 'No events'
          }
        }
      },
      detailVisible: false,
      selectedEvent: null,
      loading: false,
      currentViewTitle: '',
      cancelDialogVisible: false,
      cancelling: false,
      cancelForm: {
        reservationCode: ''
      },
      cancelRules: {
        reservationCode: [{
          required: true,
          message: this.$t('reservation.codeOrContactRequired'),
          trigger: 'blur'
        }]
      }
    };
  },
  mounted() {
    this.loadEvents();
  },
  beforeDestroy() {
    // 清除资源
  },
  watch: {
    // 监听语言变化
    '$i18n.locale': {
      handler(newLocale) {
        if (this.$refs.fullCalendar) {
          const calendarApi = this.$refs.fullCalendar.getApi();
          calendarApi.setOption('locale', newLocale === 'zh-CN' ? 'zh-cn' : 'en');
          this.currentViewTitle = calendarApi.view.title;
        }
      },
      immediate: true
    }
  },
  methods: {
    // 加载预约数据
    async loadEvents() {
      this.loading = true;
      try {
        const calendarApi = this.$refs.fullCalendar.getApi();
        const start = this.formatDate(calendarApi.view.activeStart);
        const end = this.formatDate(calendarApi.view.activeEnd);
        const response = await this.$http.get('/api/reservations/calendar', {
          params: {
            start_date: start,
            end_date: end
          }
        });
        if (response.data.success) {
          calendarApi.removeAllEvents();
          calendarApi.addEventSource(response.data.events);
        } else {
          this.$message.error(response.data.message || this.$t('calendar.loadFailed'));
        }
      } catch (error) {
        console.error('Failed to load calendar events:', error);
        this.$message.error(this.$t('calendar.loadFailed'));
      } finally {
        this.loading = false;
      }
    },
    // 处理日期范围变化
    handleDatesSet() {
      // 更新当前视图标题
      const calendarApi = this.$refs.fullCalendar.getApi();
      this.currentViewTitle = calendarApi.view.title;

      // 加载事件数据
      this.loadEvents();
    },
    // 处理事件点击
    handleEventClick(info) {
      this.selectedEvent = info.event;
      this.detailVisible = true;
    },
    // 处理事件渲染
    handleEventDidMount(info) {
      // 获取事件状态
      const status = info.event.extendedProps.status;

      // 根据状态设置事件颜色（适用于所有视图）
      if (status) {
        if (status === 'confirmed') {
          info.el.style.backgroundColor = 'rgba(103, 194, 58, 0.7)'; // 已确认 - 半透明绿色
          info.el.style.borderColor = 'rgba(103, 194, 58, 0.9)';
        } else if (status === 'in_use') {
          info.el.style.backgroundColor = 'rgba(64, 158, 255, 0.7)'; // 使用中 - 半透明蓝色
          info.el.style.borderColor = 'rgba(64, 158, 255, 0.9)';
        }
      }

      // 添加鼠标悬停工具提示
      this.addEventTooltip(info);

      // 为循环预约添加标记 - 适用于所有视图
      if (info.event.extendedProps.isRecurring) {
        // 月视图的处理方式
        if (info.view.type === 'dayGridMonth') {
          const recurringIcon = document.createElement('span');
          recurringIcon.className = 'recurring-icon';
          recurringIcon.innerHTML = '<i class="el-icon-refresh-right"></i>';
          const titleEl = info.el.querySelector('.fc-event-title');
          if (titleEl) {
            titleEl.appendChild(recurringIcon);
          }
        }
        // 周视图和日视图的处理方式
        else if (info.view.type === 'timeGridWeek' || info.view.type === 'timeGridDay') {
          // 为事件添加循环图标标记
          const recurringIcon = document.createElement('span');
          recurringIcon.className = 'recurring-icon-timegrid';
          recurringIcon.innerHTML = '<i class="el-icon-refresh-right"></i>';

          // 添加到自定义内容中
          const eventContent = info.el.querySelector('.custom-event-content');
          if (eventContent) {
            eventContent.appendChild(recurringIcon);
          } else {
            // 如果还没有自定义内容，添加到事件主体
            const mainContent = info.el.querySelector('.fc-event-main');
            if (mainContent) {
              mainContent.appendChild(recurringIcon);
            }
          }
        }
      }

      // 在月视图中修改时间格式为24小时制
      if (info.view.type === 'dayGridMonth') {
        const timeEl = info.el.querySelector('.fc-event-time');
        if (timeEl && info.event.start) {
          // 使用24小时制格式化时间
          const formattedTime = info.event.start.toLocaleTimeString('zh-CN', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          });
          timeEl.textContent = formattedTime;
        }
      }

      // 在周视图和日视图中自定义事件内容
      if (info.view.type === 'timeGridWeek' || info.view.type === 'timeGridDay') {
        // 清除默认内容
        const eventContent = info.el.querySelector('.fc-event-main');
        if (eventContent) {
          // 创建自定义内容
          const customContent = document.createElement('div');
          customContent.className = 'custom-event-content';

          // 设备名称
          const title = document.createElement('div');
          title.className = 'event-title';
          title.textContent = info.event.title;
          customContent.appendChild(title);

          // 使用人
          const user = document.createElement('div');
          user.className = 'event-user';
          user.textContent = info.event.extendedProps.userName || '';
          customContent.appendChild(user);

          // 时间
          const time = document.createElement('div');
          time.className = 'event-time';
          const startTime = info.event.start ? info.event.start.toLocaleTimeString('zh-CN', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          }) : '';
          const endTime = info.event.end ? info.event.end.toLocaleTimeString('zh-CN', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          }) : '';
          time.textContent = `${startTime}-${endTime}`;
          customContent.appendChild(time);

          // 替换内容
          eventContent.innerHTML = '';
          eventContent.appendChild(customContent);

          // 为循环预约添加图标
          if (info.event.extendedProps.isRecurring) {
            const recurringIcon = document.createElement('span');
            recurringIcon.className = 'recurring-icon-timegrid';
            recurringIcon.innerHTML = '<i class="el-icon-refresh-right"></i>';
            eventContent.appendChild(recurringIcon);
          }
        }
      }

      // 如果是日视图，优化紧凑型布局
      if (info.view.type === 'timeGridDay') {
        // 优化日视图中的显示文本，使用行内布局
        const eventContent = info.el.querySelector('.fc-event-main');
        if (eventContent) {
          // 替换自定义内容为更紧凑的布局
          const compactContent = document.createElement('div');
          compactContent.className = 'custom-event-content compact';

          // 创建包含所有信息的行内布局
          const inlineContent = document.createElement('div');
          inlineContent.className = 'inline-content';

          // 设备名称
          const titleSpan = document.createElement('span');
          titleSpan.className = 'event-title-inline';
          titleSpan.textContent = info.event.title;
          inlineContent.appendChild(titleSpan);

          // 添加分隔符
          const separator1 = document.createElement('span');
          separator1.className = 'event-separator';
          separator1.textContent = ' · ';
          inlineContent.appendChild(separator1);

          // 使用人
          const userSpan = document.createElement('span');
          userSpan.className = 'event-user-inline';
          userSpan.textContent = info.event.extendedProps.userName || '';
          inlineContent.appendChild(userSpan);

          // 添加分隔符
          const separator2 = document.createElement('span');
          separator2.className = 'event-separator';
          separator2.textContent = ' · ';
          inlineContent.appendChild(separator2);

          // 时间
          const timeSpan = document.createElement('span');
          timeSpan.className = 'event-time-inline';
          const startTime = info.event.start ? info.event.start.toLocaleTimeString('zh-CN', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          }) : '';
          const endTime = info.event.end ? info.event.end.toLocaleTimeString('zh-CN', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          }) : '';
          timeSpan.textContent = `${startTime}-${endTime}`;
          inlineContent.appendChild(timeSpan);
          compactContent.appendChild(inlineContent);

          // 替换内容
          eventContent.innerHTML = '';
          eventContent.appendChild(compactContent);

          // 为循环预约添加图标
          if (info.event.extendedProps.isRecurring) {
            const recurringIcon = document.createElement('span');
            recurringIcon.className = 'recurring-icon-timegrid';
            recurringIcon.innerHTML = '<i class="el-icon-refresh-right"></i>';
            eventContent.appendChild(recurringIcon);
          }
        }
      }

      // 如果是周视图，优化紧凑型布局
      if (info.view.type === 'timeGridWeek') {
        // 优化周视图中的显示文本，根据宽度动态调整布局
        const eventRect = info.el.getBoundingClientRect();
        const eventWidth = eventRect.width;

        // 如果事件宽度小于一定值，使用更紧凑的布局
        if (eventWidth < 120) {
          const eventContent = info.el.querySelector('.fc-event-main');
          if (eventContent) {
            // 替换自定义内容为更紧凑的布局
            const compactContent = document.createElement('div');
            compactContent.className = 'custom-event-content compact';

            // 只显示设备名称和使用人
            const inlineContent = document.createElement('div');
            inlineContent.className = 'inline-content';

            // 设备名称
            const titleSpan = document.createElement('span');
            titleSpan.className = 'event-title-inline';
            titleSpan.textContent = info.event.title;
            inlineContent.appendChild(titleSpan);

            // 添加分隔符
            const separator = document.createElement('span');
            separator.className = 'event-separator';
            separator.textContent = ' · ';
            inlineContent.appendChild(separator);

            // 使用人
            const userSpan = document.createElement('span');
            userSpan.className = 'event-user-inline';
            userSpan.textContent = info.event.extendedProps.userName || '';
            inlineContent.appendChild(userSpan);
            compactContent.appendChild(inlineContent);

            // 时间作为第二行
            const timeContent = document.createElement('div');
            timeContent.className = 'time-content';
            const timeSpan = document.createElement('span');
            timeSpan.className = 'event-time-inline';
            const startTime = info.event.start ? info.event.start.toLocaleTimeString('zh-CN', {
              hour: '2-digit',
              minute: '2-digit',
              hour12: false
            }) : '';
            const endTime = info.event.end ? info.event.end.toLocaleTimeString('zh-CN', {
              hour: '2-digit',
              minute: '2-digit',
              hour12: false
            }) : '';
            timeSpan.textContent = `${startTime}-${endTime}`;
            timeContent.appendChild(timeSpan);
            compactContent.appendChild(timeContent);

            // 替换内容
            eventContent.innerHTML = '';
            eventContent.appendChild(compactContent);

            // 为循环预约添加图标
            if (info.event.extendedProps.isRecurring) {
              const recurringIcon = document.createElement('span');
              recurringIcon.className = 'recurring-icon-timegrid';
              recurringIcon.innerHTML = '<i class="el-icon-refresh-right"></i>';
              eventContent.appendChild(recurringIcon);
            }
          }
        }
      }
    },
    // 切换视图
    changeView(viewName) {
      const calendarApi = this.$refs.fullCalendar.getApi();
      calendarApi.changeView(viewName);
    },
    // 跳转到今天
    today() {
      const calendarApi = this.$refs.fullCalendar.getApi();
      calendarApi.today();
    },
    // 上一个时间段
    prev() {
      const calendarApi = this.$refs.fullCalendar.getApi();
      calendarApi.prev();
    },
    // 下一个时间段
    next() {
      const calendarApi = this.$refs.fullCalendar.getApi();
      calendarApi.next();
    },
    // 获取状态标签类型
    getStatusTagType(status) {
      const statusMap = {
        'confirmed': 'success',
        'in_use': 'primary',
        'cancelled': 'info',
        'expired': 'danger'
      };
      return statusMap[status] || 'info';
    },
    // 格式化日期
    formatDate(date) {
      return `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')}`;
    },
    // 格式化日期时间
    formatDateTime(date) {
      return formatDate(date, 'YYYY-MM-DD HH:mm');
    },
    // 格式化短时间（只显示小时和分钟）
    formatShortTime(date) {
      if (!date) return '';
      return date.toLocaleTimeString('zh-CN', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      });
    },
    // 获取状态文本
    getStatusText(status) {
      const statusMap = {
        'confirmed': this.$t('reservation.confirmed'),
        'in_use': this.$t('reservation.inUse'),
        'cancelled': this.$t('reservation.cancelled'),
        'expired': this.$t('reservation.expired')
      };
      return statusMap[status] || status;
    },
    // 添加事件工具提示
    addEventTooltip(info) {
      // 获取事件信息
      const event = info.event;
      const props = event.extendedProps;

      // 计算事件持续时间（分钟）
      const start = event.start;
      const end = event.end;
      const durationMs = end - start;
      const durationMinutes = Math.round(durationMs / (1000 * 60));

      // 获取当前视图类型
      const calendarApi = this.$refs.fullCalendar.getApi();
      const viewType = calendarApi.view.type;

      // 获取事件元素
      const titleEl = info.el.querySelector('.fc-event-title');
      const timeEl = info.el.querySelector('.fc-event-time');
      const mainEl = info.el.querySelector('.fc-event-main');

      // 根据视图类型处理事件显示
      if (viewType === 'dayGridMonth') {
        // 月视图：所有预约显示相同，显示设备名称+开始和结束时间
        if (titleEl) {
          // 提取完整设备名称（通常是前两个单词，如"M2F Mic"）
          const titleParts = event.title.split(' ');
          let deviceName = titleParts[0]; // 至少包含第一个单词

          // 如果第二个单词是"Mic"或其他设备类型标识，也包含它
          if (titleParts.length > 1 && (titleParts[1] === 'Mic' || titleParts[1] === 'Speaker' || titleParts[1].startsWith('Speaker'))) {
            deviceName = `${titleParts[0]} ${titleParts[1]}`;
          }

          // 格式化时间
          const startTime = this.formatShortTime(start);
          const endTime = this.formatShortTime(end);

          // 设置标题为"设备名称 开始-结束"
          titleEl.textContent = `${deviceName}     ${startTime}-${endTime}`;

          // 完全移除时间元素，防止重复显示时间
          if (timeEl) {
            timeEl.remove(); // 彻底移除时间元素，而不仅仅是隐藏
          }
        }
      } else if (viewType === 'timeGridWeek' || viewType === 'timeGridDay') {
        // 周视图和日视图：短时间预约特殊处理
        if (durationMinutes <= 30) {
          // 添加短时间预约的类
          info.el.classList.add('very-short-duration-event');

          // 只显示设备名称
          if (titleEl) {
            const deviceName = event.title.split(' ')[0];
            titleEl.textContent = deviceName;
          }

          // 调整主容器样式
          if (mainEl) {
            mainEl.style.display = 'flex';
            mainEl.style.alignItems = 'center';
            mainEl.style.justifyContent = 'center';
            mainEl.style.padding = '2px';

            // 日视图和周视图的不同布局
            if (viewType === 'timeGridDay') {
              // 日视图：水平排列
              mainEl.style.flexDirection = 'row';
              if (timeEl) {
                timeEl.style.marginRight = '4px';
              }
            } else {
              // 周视图：垂直排列
              mainEl.style.flexDirection = 'column';
              if (timeEl) {
                timeEl.style.marginBottom = '2px';
              }
            }
          }

          // 调整字体样式
          if (titleEl) {
            titleEl.style.fontWeight = 'bold';
            titleEl.style.fontSize = '0.9em';
          }
          if (timeEl) {
            timeEl.style.fontWeight = 'bold';
            timeEl.style.fontSize = '0.9em';
          }
        }
      }

      // 设置title属性（原生浏览器工具提示）
      const tooltipText = `${event.title}\n${props.userName || ''}\n${this.formatDateTime(start)} - ${this.formatDateTime(end)}\n${this.getStatusText(props.status)}\n${props.reservationNumber}`;
      info.el.setAttribute('title', tooltipText);
    },
    // 显示取消预约对话框
    showCancelDialog() {
      this.cancelForm.reservationCode = '';
      this.cancelDialogVisible = true;
    },
    // 取消预约
    async cancelReservation() {
      try {
        // 验证表单
        await this.$refs.cancelForm.validate();

        // 检查输入的预约码是否与当前预约码匹配
        if (this.cancelForm.reservationCode !== this.selectedEvent.extendedProps.reservationCode) {
          this.$message.error(this.$t('reservation.checkCodeAndContact'));
          return;
        }
        this.cancelling = true;

        // 准备请求数据
        const data = {
          reservation_number: this.selectedEvent.extendedProps.reservationNumber || null,
          lang: this.$i18n.locale
        };

        // 调用取消预约API
        const response = await reservationApi.cancelReservation(this.selectedEvent.extendedProps.reservationCode, data);
        if (response.data.success) {
          this.$message.success(this.$t('reservation.cancelSuccess'));
          this.cancelDialogVisible = false;

          // 关闭预约详情弹窗
          this.detailVisible = false;

          // 重新加载日历事件
          this.loadEvents();
        } else {
          this.$message.error(response.data.message || this.$t('reservation.cancelFailed'));
        }
      } catch (error) {
        console.error('Failed to cancel reservation:', error);
        this.$message.error(this.$t('error.serverError'));
      } finally {
        this.cancelling = false;
      }
    }
  }
};
