// import { Calendar } from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import FullCalendar from '@fullcalendar/vue';
import { formatDate } from '@/utils/date';
import reservationApi from '@/api/reservation';
export default {
  name: 'CalendarView',
  components: {
    FullCalendar
  },
  data() {
    return {
      calendarOptions: {
        plugins: [dayGridPlugin, timeGridPlugin, interactionPlugin],
        initialView: 'dayGridMonth',
        headerToolbar: false,
        // 我们使用自定义的头部控件
        events: [],
        // 将通过API加载
        eventClick: this.handleEventClick,
        eventDidMount: this.handleEventDidMount,
        datesSet: this.handleDatesSet,
        locale: this.$i18n.locale === 'zh-CN' ? 'zh-cn' : 'en',
        firstDay: 1,
        // 周一作为一周的第一天
        allDaySlot: false,
        // 不显示"全天"选项
        slotMinTime: '00:00:00',
        // 从0点开始
        slotMaxTime: '24:00:00',
        // 到24点结束
        height: 'auto',
        nowIndicator: true,
        // 显示当前时间指示器
        now: new Date('2025-04-30T13:45:00'),
        // 设置当前时间，用于测试
        slotLabelFormat: {
          hour: '2-digit',
          minute: '2-digit',
          hour12: false // 使用24小时制
        },
        slotEventOverlap: false,
        // 禁止事件重叠
        eventTimeFormat: {
          // 统一时间格式
          hour: '2-digit',
          minute: '2-digit',
          hour12: false
        },
        titleFormat: {
          year: 'numeric',
          month: 'long'
        },
        // 标题格式
        views: {
          dayGridMonth: {
            dayHeaderFormat: {
              weekday: 'long'
            },
            // 月视图只显示星期几
            fixedWeekCount: false,
            // 根据当月天数动态调整行数
            showNonCurrentDates: false // 隐藏不属于当前月份的日期
          },
          timeGridWeek: {
            dayHeaderFormat: {
              weekday: 'long',
              month: 'numeric',
              day: 'numeric',
              omitCommas: true
            } // 周视图显示完整日期
          },
          timeGridDay: {
            dayHeaderFormat: {
              weekday: 'long',
              month: 'numeric',
              day: 'numeric',
              omitCommas: true
            },
            // 日视图显示完整日期
            slotEventOverlap: false,
            // 日视图特别禁止事件重叠
            eventMaxStack: 4,
            // 最多显示4个事件，超过则显示"+更多"
            moreLinkClick: 'popover' // 点击"+更多"时显示弹窗
          }
        },
        locales: {
          'zh-cn': {
            week: {
              dow: 1,
              // 周一作为一周的第一天
              doy: 4 // 一年中第一周必须包含1月4日
            },
            buttonText: {
              today: '今天',
              month: '月',
              week: '周',
              day: '日'
            },
            weekText: '周',
            allDayText: '全天',
            moreLinkText: '更多',
            noEventsText: '没有事件'
          },
          'en': {
            week: {
              dow: 1,
              // 周一作为一周的第一天
              doy: 4 // 一年中第一周必须包含1月4日
            },
            buttonText: {
              today: 'Today',
              month: 'Month',
              week: 'Week',
              day: 'Day'
            },
            weekText: 'W',
            allDayText: 'All day',
            moreLinkText: 'more',
            noEventsText: 'No events'
          }
        }
      },
      detailVisible: false,
      selectedEvent: null,
      loading: false,
      currentViewTitle: '',
      cancelDialogVisible: false,
      cancelling: false,
      cancelForm: {
        reservationCode: ''
      },
      cancelRules: {
        reservationCode: [{
          required: true,
          message: this.$t('reservation.codeOrContactRequired'),
          trigger: 'blur'
        }]
      },
      nowIndicatorTimer: null
    };
  },
  mounted() {
    this.loadEvents();
    this.setupCustomNowIndicator();
  },
  beforeDestroy() {
    // 清除时间指示器定时器
    if (this.nowIndicatorTimer) {
      clearInterval(this.nowIndicatorTimer);
    }
  },
  watch: {
    // 监听语言变化
    '$i18n.locale': {
      handler(newLocale) {
        if (this.$refs.fullCalendar) {
          const calendarApi = this.$refs.fullCalendar.getApi();
          calendarApi.setOption('locale', newLocale === 'zh-CN' ? 'zh-cn' : 'en');
          this.currentViewTitle = calendarApi.view.title;
        }
      },
      immediate: true
    }
  },
  methods: {
    // 加载预约数据
    async loadEvents() {
      this.loading = true;
      try {
        const calendarApi = this.$refs.fullCalendar.getApi();
        const start = this.formatDate(calendarApi.view.activeStart);
        const end = this.formatDate(calendarApi.view.activeEnd);
        const response = await this.$http.get('/api/reservations/calendar', {
          params: {
            start_date: start,
            end_date: end
          }
        });
        if (response.data.success) {
          calendarApi.removeAllEvents();
          calendarApi.addEventSource(response.data.events);
        } else {
          this.$message.error(response.data.message || this.$t('calendar.loadFailed'));
        }
      } catch (error) {
        console.error('Failed to load calendar events:', error);
        this.$message.error(this.$t('calendar.loadFailed'));
      } finally {
        this.loading = false;
      }
    },
    // 处理日期范围变化
    handleDatesSet() {
      // 更新当前视图标题
      const calendarApi = this.$refs.fullCalendar.getApi();
      this.currentViewTitle = calendarApi.view.title;

      // 加载事件数据
      this.loadEvents();

      // 更新自定义时间指示器
      if (calendarApi.view.type === 'timeGridWeek') {
        this.setupCustomNowIndicator();
      }
    },
    // 设置自定义时间指示器
    setupCustomNowIndicator() {
      // 等待DOM更新完成
      this.$nextTick(() => {
        // 获取当前视图类型
        const calendarApi = this.$refs.fullCalendar.getApi();
        const viewType = calendarApi.view.type;

        // 只在周视图中自定义时间指示器
        if (viewType !== 'timeGridWeek') {
          return; // 日视图保持默认行为
        }

        // 获取当前日期 - 使用日历API的now值，这样可以与日历保持一致
        const today = calendarApi.getNow();
        const dayOfWeek = today.getDay(); // 0是周日，1是周一，...，6是周六

        // 调整为日历的星期表示（1是周一，...，7是周日）
        const calendarDayOfWeek = dayOfWeek === 0 ? 7 : dayOfWeek;
        console.log('当前日期:', today);
        console.log('星期几:', dayOfWeek);
        console.log('日历星期几:', calendarDayOfWeek);

        // 设置一个定时器，每分钟更新一次时间指示器
        if (this.nowIndicatorTimer) {
          clearInterval(this.nowIndicatorTimer);
        }
        const updateNowIndicator = () => {
          // 查找所有时间指示器线
          const nowIndicatorLines = document.querySelectorAll('.fc-timegrid-now-indicator-line');
          if (nowIndicatorLines.length === 0) {
            console.log('没有找到时间指示器线');
            return;
          }
          console.log('找到时间指示器线:', nowIndicatorLines.length);

          // 获取当前视图类型（可能在定时器执行期间已经改变）
          const currentViewType = this.$refs.fullCalendar.getApi().view.type;

          // 只在周视图中自定义时间指示器
          if (currentViewType !== 'timeGridWeek') {
            // 恢复日视图的默认样式
            nowIndicatorLines.forEach(line => {
              line.style.width = '';
              line.style.display = '';
            });
            return;
          }

          // 获取所有日期列
          const dayCols = document.querySelectorAll('.fc-timegrid-col');
          if (dayCols.length === 0) {
            console.log('没有找到日期列');
            return;
          }
          console.log('找到日期列:', dayCols.length);

          // 先清除所有列的特殊类
          dayCols.forEach(col => {
            col.classList.remove('hide-now-indicator');
          });

          // 遍历所有日期列
          dayCols.forEach((col, index) => {
            // 如果这一列的索引大于今天的星期数，隐藏时间指示器
            // 例如：今天是周三(calendarDayOfWeek=3)，则索引0,1,2(周一,周二,周三)显示线，
            // 索引3,4,5,6(周四,周五,周六,周日)隐藏线
            if (index + 1 > calendarDayOfWeek) {
              // 为这一列添加一个特殊的类，用于隐藏时间指示器
              col.classList.add('hide-now-indicator');
            }
          });

          // 添加自定义样式到页面，隐藏特定列中的时间指示器
          let style = document.getElementById('custom-now-indicator-style');
          if (!style) {
            style = document.createElement('style');
            style.id = 'custom-now-indicator-style';
            document.head.appendChild(style);
          }

          // 设置样式规则，隐藏特定列中的时间指示器
          style.textContent = `
            .fc-timegrid-col.hide-now-indicator .fc-timegrid-now-indicator-line {
              display: none !important;
            }
            .fc-timegrid-col.hide-now-indicator .fc-timegrid-now-indicator-arrow {
              display: none !important;
            }
          `;
        };

        // 立即执行一次
        updateNowIndicator();

        // 设置定时器，每秒更新一次，确保能捕获到时间指示器
        this.nowIndicatorTimer = setInterval(updateNowIndicator, 1000);

        // 30秒后改为每分钟更新一次
        setTimeout(() => {
          clearInterval(this.nowIndicatorTimer);
          this.nowIndicatorTimer = setInterval(updateNowIndicator, 60000);
        }, 30000);
      });
    },
    // 处理事件点击
    handleEventClick(info) {
      this.selectedEvent = info.event;
      this.detailVisible = true;
    },
    // 处理事件渲染
    handleEventDidMount(info) {
      // 获取事件状态
      const status = info.event.extendedProps.status;

      // 根据状态设置事件颜色（适用于所有视图）
      if (status) {
        if (status === 'confirmed') {
          info.el.style.backgroundColor = 'rgba(103, 194, 58, 0.7)'; // 已确认 - 半透明绿色
          info.el.style.borderColor = 'rgba(103, 194, 58, 0.9)';
        } else if (status === 'in_use') {
          info.el.style.backgroundColor = 'rgba(64, 158, 255, 0.7)'; // 使用中 - 半透明蓝色
          info.el.style.borderColor = 'rgba(64, 158, 255, 0.9)';
        }
      }

      // 为循环预约添加标记 - 适用于所有视图
      if (info.event.extendedProps.isRecurring) {
        // 月视图的处理方式
        if (info.view.type === 'dayGridMonth') {
          const recurringIcon = document.createElement('span');
          recurringIcon.className = 'recurring-icon';
          recurringIcon.innerHTML = '<i class="el-icon-refresh-right"></i>';
          const titleEl = info.el.querySelector('.fc-event-title');
          if (titleEl) {
            titleEl.appendChild(recurringIcon);
          }
        }
        // 周视图和日视图的处理方式
        else if (info.view.type === 'timeGridWeek' || info.view.type === 'timeGridDay') {
          // 为事件添加循环图标标记
          const recurringIcon = document.createElement('span');
          recurringIcon.className = 'recurring-icon-timegrid';
          recurringIcon.innerHTML = '<i class="el-icon-refresh-right"></i>';

          // 添加到自定义内容中
          const eventContent = info.el.querySelector('.custom-event-content');
          if (eventContent) {
            eventContent.appendChild(recurringIcon);
          } else {
            // 如果还没有自定义内容，添加到事件主体
            const mainContent = info.el.querySelector('.fc-event-main');
            if (mainContent) {
              mainContent.appendChild(recurringIcon);
            }
          }
        }
      }

      // 在月视图中修改时间格式为24小时制
      if (info.view.type === 'dayGridMonth') {
        const timeEl = info.el.querySelector('.fc-event-time');
        if (timeEl && info.event.start) {
          // 使用24小时制格式化时间
          const formattedTime = info.event.start.toLocaleTimeString('zh-CN', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          });
          timeEl.textContent = formattedTime;
        }
      }

      // 在周视图和日视图中自定义事件内容
      if (info.view.type === 'timeGridWeek' || info.view.type === 'timeGridDay') {
        // 清除默认内容
        const eventContent = info.el.querySelector('.fc-event-main');
        if (eventContent) {
          // 创建自定义内容
          const customContent = document.createElement('div');
          customContent.className = 'custom-event-content';

          // 设备名称
          const title = document.createElement('div');
          title.className = 'event-title';
          title.textContent = info.event.title;
          customContent.appendChild(title);

          // 使用人
          const user = document.createElement('div');
          user.className = 'event-user';
          user.textContent = info.event.extendedProps.userName || '';
          customContent.appendChild(user);

          // 时间
          const time = document.createElement('div');
          time.className = 'event-time';
          const startTime = info.event.start ? info.event.start.toLocaleTimeString('zh-CN', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          }) : '';
          const endTime = info.event.end ? info.event.end.toLocaleTimeString('zh-CN', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          }) : '';
          time.textContent = `${startTime}-${endTime}`;
          customContent.appendChild(time);

          // 替换内容
          eventContent.innerHTML = '';
          eventContent.appendChild(customContent);

          // 为循环预约添加图标
          if (info.event.extendedProps.isRecurring) {
            const recurringIcon = document.createElement('span');
            recurringIcon.className = 'recurring-icon-timegrid';
            recurringIcon.innerHTML = '<i class="el-icon-refresh-right"></i>';
            eventContent.appendChild(recurringIcon);
          }
        }
      }
    },
    // 切换视图
    changeView(viewName) {
      const calendarApi = this.$refs.fullCalendar.getApi();
      calendarApi.changeView(viewName);
    },
    // 跳转到今天
    today() {
      const calendarApi = this.$refs.fullCalendar.getApi();
      calendarApi.today();
    },
    // 上一个时间段
    prev() {
      const calendarApi = this.$refs.fullCalendar.getApi();
      calendarApi.prev();
    },
    // 下一个时间段
    next() {
      const calendarApi = this.$refs.fullCalendar.getApi();
      calendarApi.next();
    },
    // 获取状态标签类型
    getStatusTagType(status) {
      const statusMap = {
        'confirmed': 'success',
        'in_use': 'primary',
        'cancelled': 'info',
        'expired': 'danger'
      };
      return statusMap[status] || 'info';
    },
    // 格式化日期
    formatDate(date) {
      return `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')}`;
    },
    // 格式化日期时间
    formatDateTime(date) {
      return formatDate(date, 'YYYY-MM-DD HH:mm');
    },
    // 获取状态文本
    getStatusText(status) {
      const statusMap = {
        'confirmed': this.$t('reservation.confirmed'),
        'in_use': this.$t('reservation.inUse'),
        'cancelled': this.$t('reservation.cancelled'),
        'expired': this.$t('reservation.expired')
      };
      return statusMap[status] || status;
    },
    // 显示取消预约对话框
    showCancelDialog() {
      this.cancelForm.reservationCode = '';
      this.cancelDialogVisible = true;
    },
    // 取消预约
    async cancelReservation() {
      try {
        // 验证表单
        await this.$refs.cancelForm.validate();

        // 检查输入的预约码是否与当前预约码匹配
        if (this.cancelForm.reservationCode !== this.selectedEvent.extendedProps.reservationCode) {
          this.$message.error(this.$t('reservation.checkCodeAndContact'));
          return;
        }
        this.cancelling = true;

        // 准备请求数据
        const data = {
          reservation_number: this.selectedEvent.extendedProps.reservationNumber || null,
          lang: this.$i18n.locale
        };

        // 调用取消预约API
        const response = await reservationApi.cancelReservation(this.selectedEvent.extendedProps.reservationCode, data);
        if (response.data.success) {
          this.$message.success(this.$t('reservation.cancelSuccess'));
          this.cancelDialogVisible = false;

          // 关闭预约详情弹窗
          this.detailVisible = false;

          // 重新加载日历事件
          this.loadEvents();
        } else {
          this.$message.error(response.data.message || this.$t('reservation.cancelFailed'));
        }
      } catch (error) {
        console.error('Failed to cancel reservation:', error);
        this.$message.error(this.$t('error.serverError'));
      } finally {
        this.cancelling = false;
      }
    }
  }
};
